let selectDiv = document.getElementById('selection');
selectDiv.addEventListener('change', function (e) {

    switch (e.target.value) {
        case 'zero':
            number = 0
            break;
        case 'one':
            number = 1
            break;
        case 'two':
            number = 2
            break;
        case 'three':
            number = 3
            break;
        case 'four':
            number = 4
            break;
        case 'five':
            number = 5
            break;
        case 'six':
            number = 6
            break;
        case 'seven':
            number = 7
            break;
        case 'eight':
            number = 8
            break;
        case 'nine':
            number = 9
            break;
        case 'ten':
            number = 10
            break;
        case 'eleven':
            number = 11
            break;
        case 'twelve':
            number = 12
            break;
    }
    RunMain()
});


function RunMain() {
    pbptable = document.getElementById("playByPlayTable")
    for (let i = (pbptable.rows.length - 1); i > 0; i--) {
        pbptable.deleteRow(i)
    }
    for (i = 0; i < PlayByPlay.Time.length; i++) {
        for (x = 0; x < BreakPositions.length; x++) {
            if (BreakPositions[x] == i) {
                row = pbptable.insertRow(-1);
                row.id = i
                row.classList.add("pbp")
                var cell1 = row.insertCell(0);

                cell1.innerHTML = "END Q" + (x + 1)
                cell1.style.fontSize = "2em"
                cell1.style.lineHeight = "2em"
                cell1.colSpan = "3"

            }
        }
        if ((PlayByPlay.Player[i] == BoxScore.Name[number - 1]) || (number == 0)) {

            row = pbptable.insertRow(-1);
            row.id = i
            row.classList.add("pbp")
            var cell1 = row.insertCell(0);
            var cell2 = row.insertCell(1);
            var cell3 = row.insertCell(2);

            cell1.innerHTML = PlayByPlay.Time[i];
            cell2.innerHTML = PlayByPlay.Player[i];
            cell3.innerHTML = PlayByPlay.Thing[i];
        } else if (number == 11) {
            for (x=0; x<5; x++) {
                if ((PlayByPlay.Player[i] == BoxScore.Name[x])) {

                    row = pbptable.insertRow(-1);
                    row.id = i
                    row.classList.add("pbp")
                    var cell1 = row.insertCell(0);
                    var cell2 = row.insertCell(1);
                    var cell3 = row.insertCell(2);
        
                    cell1.innerHTML = PlayByPlay.Time[i];
                    cell2.innerHTML = PlayByPlay.Player[i];
                    cell3.innerHTML = PlayByPlay.Thing[i];
                }
            }
        } else if (number == 12) {
            for (x=5; x<10; x++) {
                if ((PlayByPlay.Player[i] == BoxScore.Name[x])) {

                    row = pbptable.insertRow(-1);
                    row.id = i
                    row.classList.add("pbp")
                    var cell1 = row.insertCell(0);
                    var cell2 = row.insertCell(1);
                    var cell3 = row.insertCell(2);
        
                    cell1.innerHTML = PlayByPlay.Time[i];
                    cell2.innerHTML = PlayByPlay.Player[i];
                    cell3.innerHTML = PlayByPlay.Thing[i];
                }
            }
        }

    }

}

